export default {
  data() {
    return {
      step: 0,
      questionStep: 0,
      allQuestions: questions,
      userAnswers: [],
      totalGrade: 0
    }
  },
  components: {
    Question
  },
  methods: {
    startTest: function() {
      this.step = 1;
    },

    nextQuestion: function(answer) {
      this.userAnswers.push(answer.isRight);

      if (this.questionStep === this.allQuestions.length - 1) {
        this.finishTest();
        this.step = 2;
        this.editProgressBar();
        return;
      }

      this.questionStep++;
      this.editProgressBar();
    },

    editProgressBar: function() {
      const ALL_QEUSTION = this.allQuestions.length;
      let progressBar = document.querySelector('.progress-bar');
      let stepProgress = 100 / ALL_QEUSTION;
      let nowProgress = parseInt(progressBar.style.width);

      progressBar.style.width = stepProgress + '%';
      progressBar.textContent = Math.floor(nowProgress) + '%';

      console.log(progressBar.style.width)
    },

    finishTest: function() {
      const ALL_QEUSTION = this.userAnswers.length;
      let correctAnswers = 0;
      let totalGrade = 0;

      for(let i = 0; i < this.userAnswers.length; i++) {
        if(this.userAnswers[i] === true) {
          correctAnswers++;
        }
      }
      totalGrade = 100 / (ALL_QEUSTION / correctAnswers) / 5 / 4;
      this.totalGrade = totalGrade.toFixed(1);
    },

    resetTest: function() {
      let progressBar = document.querySelector('.progress-bar');

      this.step = 0;
      this.questionStep = 0;
      progressBar.style.width = 0 + '%';
      progressBar.textContent = 0 + '%';
    }
  },
  mounted() {
